* {
  margin: 0;
  font-family: "Poppins", serif;
  border: None;
  text-decoration: None;
}

body {
  font-family: Arial, sans-serif;
  margin: 0;
  padding: 0;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #f4f4f9;
}

.chat-container {
  width: 100%;
  height: 600px;
  background: #fff;
  border-radius: 10px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  display: flex;
  flex-direction: column;
}

.chat-header {
  padding: 10px 15px;
  background: linear-gradient(to right, #00F2FE 21%, #4FACFE);
  color: #fff;
  text-align: center;
  border-top-left-radius: 10px;
  border-top-right-radius: 10px;
}

.chat-body {
  display: block;
  min-height: 90%;
  flex: 1;
  padding: 10px;
  overflow-y: auto;
  display: flex;
  flex-direction: column;
  gap: 10px;
}

.message {
  max-width: 70%;
  padding: 10px;
  border-radius: 10px;
  font-size: 14px;
}

.message.received {
  background: #f1f0f0;
  align-self: flex-start;
}

.message.sent {
  background: linear-gradient(to right, #00F2FE 10%, #4FACFE);
  color: white;
  align-self: flex-end;
}

.chat-footer {
  display: flex;
  padding: 10px;
  background-color: #f9f9f9;
  border-top: 1px solid #ddd;
}

#chat-input {
  flex: 1;
  padding: 10px;
  border: 1px solid #ddd;
  border-radius: 10px;
  margin-right: 10px;
  font-size: 14px;
}

#send-button {
  background: linear-gradient(to right, #00F2FE 21%, #4FACFE);
  color: white;
  border: none;
  padding: 10px 15px;
  border-radius: 5px;
  cursor: pointer;
  font-size: 14px;
}

#send-button:hover {
  background: #5300d6;
}

