@import url("https://fonts.googleapis.com/css2?family=Poppins:ital,wght@0,100;0,200;0,300;0,400;0,500;0,600;0,700;0,800;0,900;1,100;1,200;1,300;1,400;1,500;1,600;1,700;1,800;1,900&display=swap");
* {
  margin: 0;
  font-family: "Poppins", sans-serif;
  text-decoration: none;
}

.header {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 80px;
  background: transparent;
  backdrop-filter: blur(10px);
  z-index: 50;
}

.header-container {
  max-width: 1120px;
  margin: 0 auto;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0 16px;
}

.logo-container {
  display: flex;
  align-items: center;
}

.logo-text {
  margin-left: 8px;
  font-size: 20px;
  font-weight: 600;
  background: linear-gradient(to bottom, #4FACFE 21%, #00F2FE);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}

.menu-icon {
  font-size: 20px;
  color: #6C7580;
  display: none;
  position: absolute;
  right: 16px;
  cursor: pointer;
}

.nav {
  display: flex;
  align-items: center;
}

.nav-link {
  margin-left: 32px;
  font-size: 16px;
  color: #6C7580;
  text-decoration: none;
}

.get-started {
  margin-left: 32px;
  height: 40px;
  padding: 0 24px;
  display: flex;
  align-items: center;
  background: linear-gradient(to right, #00F2FE 21%, #4FACFE);
  border-radius: 9999px;
}

.get-started-text {
  color: white;
  font-weight: 600;
  text-align: center;
}

.close-icon {
  position: absolute;
  top: 24px;
  right: 24px;
  font-size: 24px;
  color: #6C7580;
  cursor: pointer;
  display: none;
}

/* Responsive styles */
@media (max-width: 1024px) {
  .menu-icon {
    display: block;
  }
  .nav {
    position: absolute;
    top: 0;
    right: 0;
    width: 80%;
    height: 100vh;
    background: white;
    flex-direction: column;
    padding-top: 80px;
    transform: translateX(100%);
    transition: transform 0.5s ease-in-out;
  }
  .show-menu {
    transform: translateX(0);
  }
  .hide-menu {
    transform: translateX(100%);
  }
  .close-icon {
    display: block;
  }
  .nav-link {
    font-size: 18px;
    margin: 16px 0;
  }
  .get-started {
    margin: 16px auto;
    width: 80%;
    font-size: 20px;
    justify-content: center;
  }
}

