appsmith.onReady(() => {
  // Lấy thông tin role từ appsmith.model
  const role = appsmith.model.role;
  let navLinks = "";

  // Nếu chưa có role hoặc role = "undefined", hiển thị các link cho khách
  if (!role || role === "undefined") {
    navLinks = `
      <p class="nav-link" id="startedLink">Cộng đồng</p>
      <p class="nav-link" id="loginLink">Đăng nhập</p>
    `;
  }
  // Nếu role là Admin
  else if (role === "Admin") {
    navLinks = `
      <p class="nav-link" id="eopLink">Kế hoạch</p>
      <p class="nav-link" id="chatLink">Nhóm trò chuyện</p>
      <p class="nav-link" id="volunteerLink">Tình nguyện</p>
      <p class="nav-link" id="goodsLink">Tài nguyên</p>
      <p class="nav-link" id="notificationLink">Duyệt bài</p>
    `;
  }
  // Nếu role là Volunteer
  else if (role === "Volunteer") {
    navLinks = `
      <p class="nav-link" id="eopLink">Kế hoạch</p>
      <p class="nav-link" id="chatLink">Nhóm chat</p>
    `;
  }
  // Nếu đã đăng nhập (role khác undefined) thì thêm link đăng xuất
  if (role && role !== "undefined") {
    navLinks += `<p class="nav-link" id="logoutLink">Đăng xuất</p>`;
  }

  // Tạo HTML header
  const headerHTML = `
    <header class="header">
      <div class="header-container">
        <div class="logo-container" id="logoContainer">
          <p class="logo-text">Emergix</p>
        </div>
        <div class="menu-icon" id="menuIcon">&#9776;</div>
        <nav class="nav hide-menu" id="nav">
          <div class="close-icon" id="closeIcon">&#10005;</div>
          ${navLinks}
        </nav>
      </div>
    </header>
  `;

  // Gán nội dung vào thẻ root
  const rootEl = document.getElementById("root");
  if (!rootEl) {
    console.error("No #root element found!");
    return;
  }
  rootEl.innerHTML = headerHTML;

  // Hàm toggle menu (cho mobile)
  function toggleMenu() {
    const navEl = document.getElementById("nav");
    if (navEl.classList.contains("hide-menu")) {
      navEl.classList.remove("hide-menu");
      navEl.classList.add("show-menu");
    } else {
      navEl.classList.remove("show-menu");
      navEl.classList.add("hide-menu");
    }
  }

  // Gắn sự kiện cho menu icon và close icon
  document.getElementById("menuIcon").addEventListener("click", toggleMenu);
  document.getElementById("closeIcon").addEventListener("click", toggleMenu);

  // Sự kiện cho logo: chuyển đến Dashboard
  document.getElementById("logoContainer").addEventListener("click", () => {
    appsmith.triggerEvent("navigateToDashboard");
  });

  // Gắn sự kiện cho các link dựa trên id
  if (document.getElementById("startedLink")) {
    document.getElementById("startedLink").addEventListener("click", () => {
      appsmith.triggerEvent("navigateToCommunity");
    });
  }
  if (document.getElementById("loginLink")) {
    document.getElementById("loginLink").addEventListener("click", () => {
      appsmith.triggerEvent("navigateToLogin");
    });
  }
  if (document.getElementById("eopLink")) {
    document.getElementById("eopLink").addEventListener("click", () => {
      appsmith.triggerEvent("navigateToEOP");
    });
  }
  if (document.getElementById("chatLink")) {
    document.getElementById("chatLink").addEventListener("click", () => {
      appsmith.triggerEvent("navigateToChatGroup");
    });
  }
  if (document.getElementById("volunteerLink")) {
    document.getElementById("volunteerLink").addEventListener("click", () => {
      appsmith.triggerEvent("navigateToAddVolunteers");
    });
  }
  if (document.getElementById("goodsLink")) {
    document.getElementById("goodsLink").addEventListener("click", () => {
      appsmith.triggerEvent("navigateToAddGoods");
    });
  }
  if (document.getElementById("notificationLink")) {
    document.getElementById("notificationLink").addEventListener("click", () => {
      appsmith.triggerEvent("navigateToNotification");
    });
  }
  if (document.getElementById("logoutLink")) {
    document.getElementById("logoutLink").addEventListener("click", () => {
      appsmith.triggerEvent("logout");
    });
  }
});

