windyInit(options, windyAPI => {
  const {
    map
  } = windyAPI;

  // Bản đồ ánh xạ loại cảnh báo tới biểu tượng
  const mapping = {
    warning_earthquake: `${appsmith.model.BASE_URL}/uploads/earthquake_7d925ff76e.png`,
    warning_flood: `${appsmith.model.BASE_URL}/uploads/house_flood_water_solid_f61cf3527f.svg`,
    warning_default: './water_level.svg',
    // Biểu tượng mặc định
    warning_wind: `${appsmith.model.BASE_URL}/uploads/thumbnail_warning_wind_0b4b075615.png`,
    warning_rain: `${appsmith.model.BASE_URL}/uploads/heavy_rain_f7cfb6771e.svg`,
    community_alert: `${appsmith.model.BASE_URL}/uploads/sos_dafe00fd39.png`,
    warning_heavy_rain: `${appsmith.model.BASE_URL}/uploads/heavy_rain_f7cfb6771e.svg`,
    water_level: {
      1: `${appsmith.model.BASE_URL}/uploads/number_1_d1d09c412d.png`,
      2: `${appsmith.model.BASE_URL}/uploads/number_2_213cfc1b5f.png`,
      3: `${appsmith.model.BASE_URL}/uploads/number_3_7987de32b6.png`
    }
  };
  const getIconUrl = (type, warningLevel = null) => {
    if (type === 'water_level' && warningLevel !== null) {
      return mapping.water_level[warningLevel] || mapping.warning_default;
    }
    return mapping[type] || mapping.warning_default;
  };
  const addMarker = (lat, lon, label, type, popupInfo, warningLevel = null) => {
    const iconUrl = getIconUrl(type, warningLevel);
    const marker = L.marker([lat, lon], {
      icon: L.icon({
        iconUrl,
        iconSize: [25, 25],
        // Kích thước biểu tượng
        iconAnchor: [12, 12] // Điểm neo
      })
    }).addTo(map);
    marker.bindPopup(popupInfo);
  };
  const processWarnings = warnings => {
    warnings.forEach(warning => {
      const {
        lat,
        long,
        label,
        warning_type,
        warning_level,
        popupInfo
      } = warning;
      if (!lat || !long) {
        console.warn('Cảnh báo thiếu tọa độ:', warning);
        return;
      }
      if (warning_type === 'water_level') {
        addMarker(lat, long, label, warning_type, popupInfo, warning_level);
      } else {
        addMarker(lat, long, label, warning_type, popupInfo);
      }
    });
  };
  const fetchWarnings = async () => {
    try {
      const response = await fetch(`${appsmith.model.BASE_URL}/api/warning`);
      if (!response.ok) throw new Error(`HTTP error! Status: ${response.status}`);
      const data = await response.json();
      if (data && data.data) {
        processWarnings(data.data);
      } else {
        console.error('Phản hồi API không hợp lệ:', data);
      }
    } catch (error) {
      console.error('Lỗi khi lấy dữ liệu cảnh báo:', error);
    }
  };
  const fetchCommunity = async () => {
    try {
      // Gửi yêu cầu đến API
      const response = await fetch(`${appsmith.model.BASE_URL}/api/communities?filters[$and][0][type][$eq]=emergency_alert&filters[$and][1][location][$notNull]=true`);
      if (!response.ok) throw new Error(`HTTP error! Status: ${response.status}`);
      const data = await response.json();

      // Kiểm tra dữ liệu trả về
      if (data && data.data) {
        data.data.forEach(community => {
          const {
            title,
            content,
            createdAt,
            location: {
              lat,
              long,
              address
            }
          } = community;

          // Nếu thiếu tọa độ, bỏ qua community này
          if (!lat || !long) {
            console.warn('Dữ liệu community thiếu tọa độ:', community);
            return;
          }

          // Thêm marker vào bản đồ
          addMarker(lat, long, title, 'community_alert', `
                        <div>
                            <h3>${title}</h3>
                            <p>${content}</p>
														<p><strong>Thời gian:</strong> ${createdAt}</p>
                            <p><strong>Địa chỉ:</strong> ${address}</p>
                        </div>
                    `);
        });
      } else {
        console.error('Phản hồi API không hợp lệ:', data);
      }
    } catch (error) {
      console.error('Lỗi khi lấy dữ liệu cộng đồng:', error);
    }
  };
  fetchCommunity();
  fetchWarnings();
});

