srcDoc.css:
* {
  margin: 0;
  font-family: "Inter", sans-serif;
  border: none;
  text-decoration: none;
}

p {
  cursor: pointer;
}

/* Footer styles */
.footer {
  padding: 20px;
  text-align: center;
  background-color: #ffffff;
}

.footer-divider {
  margin-bottom: 20px;
  border: none;
  border-top: 1px solid #e0e0e0;
}

.footer-credits {
  font-size: 16px;
  margin-bottom: 10px;
  color: #333;
}

.footer-rights {
  font-size: 14px;
  color: #666;
}

srcDoc.js:
appsmith.onReady(() => {
  // Nội dung HTML của footer
  const footerHTML = `
    <footer class="footer">
      <hr class="footer-divider" />
      <p class="footer-credits">Made by VKU.OneLove</p>
      <p class="footer-rights">© VKU.OneLove 2024. All Rights Reserved.</p>
    </footer>
  `;

  // Lấy phần tử root
  const rootEl = document.getElementById("root");
  if (rootEl) {
    // Gán nội dung HTML cho phần tử root
    rootEl.innerHTML = footerHTML;
  } else {
    console.error("Không tìm thấy phần tử với id 'root'.");
  }
});

