@import url("https://fonts.googleapis.com/css2?family=Montserrat:ital,wght@0,100..900;1,100..900&family=Poppins:ital,wght@0,100;0,200;0,300;0,400;0,500;0,600;0,700;0,800;0,900;1,100;1,200;1,300;1,400;1,500;1,600;1,700;1,800;1,900&display=swap");
* {
  margin: 0;
  font-family: "Montserrat", sans-serif;
  border: None;
  text-decoration: None;
}

p {
  cursor: pointer;
}

/* Header styles */
.header {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 80px;
  background: transparent;
  backdrop-filter: blur(10px);
  z-index: 50;
}

.header-container {
  max-width: 1520px;
  margin: 0 auto;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0 16px;
}

.logo-container {
  display: flex;
  align-items: center;
}

.logo-text {
  margin-left: 8px;
  font-size: 20px;
  font-weight: 600;
  background: linear-gradient(to bottom, #4FACFE 21%, #00F2FE);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}

.menu-icon {
  font-size: 20px;
  color: #6C7580;
  display: none;
  position: absolute;
  right: 16px;
  cursor: pointer;
}

.nav {
  display: flex;
  align-items: center;
}

.nav-link {
  margin-left: 32px;
  font-size: 16px;
  color: #6C7580;
  text-decoration: none;
  cursor: pointer;
}

.get-started {
  margin-left: 32px;
  height: 40px;
  padding: 0 24px;
  display: flex;
  align-items: center;
  background: linear-gradient(to right, #00F2FE 21%, #4FACFE);
  border-radius: 9999px;
}

.get-started-text {
  color: white;
  font-weight: 600;
  text-align: center;
}

.close-icon {
  position: absolute;
  top: 24px;
  right: 24px;
  font-size: 24px;
  color: #6C7580;
  cursor: pointer;
  display: none;
}

/* Responsive styles */

