@import url("https://fonts.googleapis.com/css2?family=Poppins:ital,wght@0,100;0,200;0,300;0,400;0,500;0,600;0,700;0,800;0,900;1,100;1,200;1,300;1,400;1,500;1,600;1,700;1,800;1,900&display=swap");
* {
  margin: 0;
  font-family: "Poppins", serif;
  border: None;
  text-decoration: None;
}

.footer {
  margin: 0;
  height: 16rem;
  margin-top: 2.5rem;
  display: flex;
  flex-direction: column;
  justify-content: center;
}

.footer-divider {
  border-top: 1px solid #bdbac0;
  margin: 0 24rem;
}

.footer-content {
  display: flex;
  height: 11rem;
  align-items: center;
  justify-content: space-between;
  padding: 0 2.5rem;
  margin: 0 20rem;
}

.footer-column {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  margin: 0.5rem 0;
}

.footer-title {
  margin-bottom: 1rem;
  font-size: 0.875rem;
  font-weight: 600;
  text-transform: uppercase;
  color: #4c5664;
}

.footer-links {
  display: flex;
  flex-direction: column;
  gap: 0.75rem;
}

.footer-link {
  font-size: 0.875rem;
  color: #6c7580;
  display: flex;
  align-items: center;
  cursor: pointer;
  text-transform: capitalize;
  transition: color 0.3s, text-decoration 0.3s;
}

.footer-link:hover {
  text-decoration: underline;
}

.footer-credits {
  font-size: 0.875rem;
  margin: 0.5rem 0;
  color: #4b5563;
  text-align: center;
}

.footer-rights {
  font-size: 0.875rem;
  color: #6c7580;
  text-align: center;
}

/* Responsive Styling */
@media (max-width: 1024px) {
  .footer {
    height: auto;
  }
  .footer-divider {
    margin: 0 2.5rem;
  }
  .footer-content {
    flex-direction: column;
    margin: 0;
    height: auto;
    align-items: flex-start;
    gap: 1.5rem;
  }
  .footer-column {
    margin: 0.5rem 0;
  }
}

