@charset "UTF-8";
/* Định dạng cho container chứa các input fields */
.input-container {
  display: flex; /* Sử dụng Flexbox */
  justify-content: space-between; /* Căn chỉnh các ô nhập đều nhau */
  margin: 10px 0; /* Khoảng cách trên và dưới */
}

/* Định dạng cho các input fields */
.input-container input {
  flex: 1; /* Chiều rộng tự động */
  padding: 10px; /* Padding cho các ô nhập */
  margin: 0 5px; /* Khoảng cách giữa các ô nhập */
  border: 1px solid #ccc; /* Đường viền */
  border-radius: 5px; /* Bo góc */
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1); /* Đổ bóng */
  font-size: 16px; /* Kích thước chữ */
}

/* Định dạng cho bản đồ */
#map {
  height: 400px; /* Chiều cao của bản đồ */
  margin-top: 20px; /* Khoảng cách trên bản đồ */
  border-radius: 5px; /* Bo góc cho bản đồ */
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2); /* Đổ bóng cho bản đồ */
}

.custom-geocoder {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  z-index: 1000; /* Đảm bảo nó nằm trên các phần tử khác */
  background-color: white; /* Màu nền */
  border: 2px solid #ccc; /* Viền */
  border-radius: 8px; /* Bo góc */
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1); /* Đổ bóng */
  padding: 10px; /* Khoảng cách bên trong */
}

srcDoc.js:
appsmith.onReady(() => {
  var map = L.map('map').setView([16.0583, 108.2210], 13); // Tọa độ Đà Nẵng

  L.tileLayer('https://tile.openstreetmap.org/{z}/{x}/{y}.png', {
    maxZoom: 19,
    attribution: '&copy; <a href="http://www.openstreetmap.org/copyright">OpenStreetMap</a>'
  }).addTo(map);
  var geocoder = L.Control.Geocoder.nominatim();
  var control = L.Control.geocoder({
    geocoder: geocoder,
    collapsed: false
  }).addTo(map);
  var geocoderContainer = control.getContainer();
  geocoderContainer.classList.add('custom-geocoder');

  // Biến để lưu trữ thông tin địa điểm
  let selectedLocation = {
    lat: null,
    lng: null,
    address: ''
  };

  // Thêm sự kiện click để hiển thị lat, long và tên địa điểm
  map.on('click', function (e) {
    var lat = e.latlng.lat;
    var lng = e.latlng.lng;
    geocoder.reverse(e.latlng, map.options.crs.scale(map.getZoom()), function (results) {
      if (results && results.length > 0) {
        var locationName = results[0].name;
        selectedLocation = {
          lat: lat,
          lng: lng,
          address: locationName
        };
        alert('Location Name: ' + locationName + '\nLatitude: ' + lat + '\nLongitude: ' + lng);
        // Cập nhật giá trị vào form
        document.getElementById('latitude-input').value = lat;
        document.getElementById('longitude-input').value = lng;
        document.getElementById('address-input').value = locationName;
        console.log();
      } else {
        alert('Could not find location name for this point.\nLatitude: ' + lat + '\nLongitude: ' + lng);
      }
    });
  });
});

