@charset "UTF-8";
#map {
  height: 400px; /* Chiều cao của bản đồ */
  margin-bottom: 20px; /* Khoảng cách dưới bản đồ */
  border-radius: 5px; /* Bo góc cho bản đồ */
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2); /* Đổ bóng cho bản đồ */
}

.info-panel {
  display: flex;
  flex-wrap: wrap;
  gap: 10px; /* Khoảng cách giữa các phần tử */
  padding: 10px;
}

.info-panel .input-group {
  display: flex;
  align-items: center;
  gap: 5px; /* Khoảng cách giữa input và button */
}

.info-panel input {
  width: 150px;
  padding: 5px;
  border: none; /* Loại bỏ border */
  border-radius: 4px;
}

.info-panel button {
  padding: 5px 8px;
  border: 2px solid #007bff; /* Viền màu xanh dương */
  background-color: white; /* Nền màu trắng */
  color: #007bff; /* Màu chữ xanh dương */
  border-radius: 4px;
  cursor: pointer;
}

.info-panel button:hover {
  background-color: #007bff; /* Nền đổi thành xanh dương khi hover */
  color: white; /* Chữ đổi thành trắng khi hover */
}

