appsmith.onReady(() => {
  // Khởi tạo bản đồ với tọa độ Đà Nẵng
  var map = L.map('map').setView([16.0583, 108.2210], 13);

  // Thêm tile layer từ OpenStreetMap
  L.tileLayer('https://tile.openstreetmap.org/{z}/{x}/{y}.png', {
    maxZoom: 19,
    attribution: '&copy; <a href="http://www.openstreetmap.org/copyright">OpenStreetMap</a>'
  }).addTo(map);

  // Khởi tạo geocoder từ thư viện Leaflet
  var geocoder = L.Control.Geocoder.nominatim();
  L.Control.geocoder({
    geocoder: geocoder,
    collapsed: false
  }).addTo(map);

  // Đảm bảo `appsmith.store` được khởi tạo
  if (!appsmith.store) {
    appsmith.store = {}; // Khởi tạo store nếu chưa tồn tại
  }

  // Đọc giá trị được lưu trữ (nếu có)
  const storedLocation = appsmith.store.selectedLocation || {
    lat: 16.0583,
    lng: 108.2210,
    address: ''
  };
  let selectedLocation = storedLocation;

  // Sự kiện click trên bản đồ
  map.on('click', e => {
    var lat = e.latlng.lat; // Latitude
    var lng = e.latlng.lng; // Longitude

    // Reverse Geocoding để lấy địa chỉ
    geocoder.reverse(e.latlng, map.options.crs.scale(map.getZoom()), results => {
      if (results && results.length > 0) {
        var locationName = results[0].name; // Địa chỉ trả về từ geocoder

        // Kiểm tra nếu giá trị mới khác với giá trị hiện tại
        if (selectedLocation.lat !== lat || selectedLocation.lng !== lng) {
          selectedLocation = {
            lat: lat,
            lng: lng,
            address: locationName
          };

          // Cập nhật các trường input qua `appsmith.updateModel`
          appsmith.updateModel({
            Input1: lat,
            // Latitude
            Input2: lng,
            // Longitude
            Input3: locationName // Address
          });

          // Lưu vào `appsmith.store`
          appsmith.store.selectedLocation = selectedLocation;

          // Gọi sự kiện `onMapClick` (nếu cần dùng trong Appsmith)
          appsmith.triggerEvent("onMapClick");
          document.getElementById('latitude-input').value = lat;
          document.getElementById('longitude-input').value = lng;
          document.getElementById('address-input').value = locationName;
          // Hiển thị thông báo (tùy chọn)
          alert(`Đã chọn địa điểm:\nTên: ${locationName}\nLatitude: ${lat}\nLongitude: ${lng}`);
        }
      } else {
        // Thông báo nếu không tìm thấy địa chỉ
        alert(`Không tìm thấy tên địa điểm cho tọa độ này.\nLatitude: ${lat}\nLongitude: ${lng}`);
      }
    });
  });
});

